import React, {useState} from 'react';
import "./newPost.css";

const Newpost = () => {

  

  return (
    <div className='write-post'>
      <div className='write-post-div'>
          <img src={img} alt='newPost-img' />
      </div>
      <form>
        <div className='newPost-file-div'>
            <label htmlFor="newPost-img" className='newPost-img-btn'>+</label>
            <input type="file" name="newPost-img" id="newPost-img" style={{ display: "none" }} />  
            <input type="text" className='write-title' placeholder="Add title ..." autoFocus={true} />
        </div>
            <textarea name="newPost-desc" id="newPost-desc" cols="30" rows="10" placeholder='Write Something ...'/>
            <button className='submitPost' type='submit'>Publish</button>
          </form>
    </div>
  )
}

export default Newpost;
