import React from 'react'
import { useContext } from 'react';
import { useRef } from 'react';
import { Link } from "react-router-dom";
import "./login.css"
import { Context } from '../../context/Context';
import axios from "axios";

const Login = () => {

  const userRef = useRef();
  const passwordRef = useRef();
  const { dispatch, isFetching } = useContext(Context);

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch({ type: "LOGIN_START" });
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        userName: userRef.current.value,
        password: passwordRef.current.value
      });
      dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
    } catch (error) {
      dispatch({ type: "LOGIN_FAILURE" });
    }
  }
  
  return (
    <div className='login'>
          <h1>Login</h1>
          <form onSubmit={handleSubmit}>
              <input type="text" name="loginEmail" id="login-email" ref={userRef} placeholder='Enter username...' autoFocus={ true } />
              <input type="password" name="loginPass" id="login-pass" ref={passwordRef} placeholder='Enter password...' />
              <button type="submit" className='login-btn' disabled={ isFetching }>Login</button>
          </form>
      <button type="button" className='register-btn'>
        <Link to="/register" className='link'>Register</Link>
      </button>
    </div>
  )
}

export default Login
