import { useEffect } from "react";
import "./tags.css";

function TagsInput(props) {

    useEffect(() => {
        props.setTag(props.tags)
        // eslintreact-hooks/exhaustive-deps
    }, [props.tags])

    return (
        <div className="tags-input-container">
            { props.tags.map((tag, index) => (
                <div className="tag-item" key={index}>
                    <span className="text">{tag}</span>
                    <span className="close" onClick={() => props.removeTag(index)}>&times;</span>
                </div>
            )) }
            <input onKeyDown={() => { props.handleKeyDown() }} type="text" className="tags-input" placeholder="Add tags ..." />
        </div>
    )
}

export default TagsInput
