import React from 'react'
import { useEffect } from 'react';
import { useLocation } from "react-router";
import "./singlePost.css";
import axios from "axios"
import { useState } from 'react';
import { Link } from "react-router-dom";
import { useContext } from 'react';
import { Context } from '../../context/Context';

const SinglePost = () => {

  window.scroll(0, 0);
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({});
  const url = "http://localhost:5000/api/posts/";
  const PF = "http://localhost:5000/images/";
  const { user } = useContext(Context);
  const [title, setTitle] = useState();
  const [desc, setDesc] = useState("");
  const [updateMode, setUpdateMode] = useState(false);


  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get(url + path);
      setPost(res.data);
      setTitle(res.data.title);
      setDesc(res.data.desc);
    }
    getPost();
  }, [path]);

  const handleDelete = async () => {
    try {
      await axios.delete("http://localhost:5000/api/posts/" + path, { data: { userName: user.userName } });
      window.location.replace("/");  
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div className='single-post'>
      <div className='single-post-head'>
        {post.photo && (<img src={PF + post.photo} className='single-post-head-img' alt='single-post-img' />)}
        {post.userName === user?.userName &&
          <div className='edit-del-btn'>
            <i className="fa-solid edit-btn fa-pen-to-square" onClick={() => { setUpdateMode(true)}} ></i>
            <i className="fa-solid del-btn fa-trash-can" onClick={handleDelete} ></i>
          </div>
        }
        { updateMode ? <input type="text" value={title} className="single-post-title-input" onChange={(e)=> setTitle(e.target.value)}/> : <h1 className='single-post-title'>{post.title}</h1>}
          </div>
          <div className='single-post-author-div'>
              <p className='single-post-author'>Author: <Link to={`/?user=${post.userName}`} className="link"><strong>{post.userName}</strong></Link></p>
        <p className='single-post-time'>{ new Date(post.createdAt).toDateString() }</p>
      </div>
      {updateMode ? <textarea value={desc} onChange={(e)=> setDesc(e.target.value)} className="single-post-desc-input"/> : (
        <div className='single-post-desc'>
          <p>{ post.desc }</p>
        </div>
        )}
    </div>
  )
}

export default SinglePost
