import React from 'react'
import { useEffect } from 'react';
import { useLocation } from "react-router";
import "./singlePost.css";
import axios from "axios"
import { useState } from 'react';
import { Link } from "react-router-dom";
import { useContext } from 'react';
import { Context } from '../../context/Context';

const SinglePost = () => {

  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({});
  const url = "http://localhost:5000/api/posts/";
  const PF = "http://localhost:5000/images/";
  const { user } = useContext(Context);


  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get(url + path);
      setPost(res.data);
    }
    getPost();
  }, [path]);

  const handleDelete = () => {
    
  }

  return (
    <div className='single-post'>
      <div className='single-post-head'>
        {post.photo && (<img src={PF + post.photo} className='single-post-head-img' alt='single-post-img' />)}
        {post.userName === user?.userName &&
          <div className='edit-del-btn'>
            <i className="fa-solid edit-btn fa-pen-to-square"></i>
            <i className="fa-solid del-btn fa-trash-can" onClick={handleDelete}></i>
          </div>
        }
        <h1 className='single-post-title'>{post.title}</h1>
          </div>
          <div className='single-post-author-div'>
              <p className='single-post-author'>Author: <Link to={`/?user=${post.userName}`} className="link"><strong>{post.userName}</strong></Link></p>
        <p className='single-post-time'>{ new Date(post.createdAt).toDateString() }</p>
          </div>
        <div className='single-post-desc'>
        <p>{ post.desc }</p>
        </div>
    </div>
  )
}

export default SinglePost
