import React from 'react'
import { Link } from "react-router-dom";
import "./post.css";

const Post = ({ post }) => {
  const PF = "http://localhost:5000/images/";
  return (
    <div className='post'>
      { post.photo && (<img src={PF + post.photo} alt='post-img' />) }
      <p className='post-category'>{post.category.map((cat) => {
        return <span>{ cat } </span>
      })}</p>
      <p className='post-date'>{new Date(post.createdAt).toDateString()}</p>
      <div className='post-title-div'>
        <Link to={`/post/${post._id}`} className="link"><p className='post-title'>{ post.title }</p></Link>
      </div>
      <p className='post-desc'>{ post.desc }</p>
    </div>
  )
}

export default Post
