import React from 'react';
import "./sidebar.css";
import aboutImg from "./aboutImg.jpg";

const Sidebar = () => {
  return (
    <div className='sidebar'>
      
      {/* about section */}

      <div className='about-section'>
        <p className='about-heading'>ABOUT</p>
        <img src={aboutImg} alt='about-img' className='about-img'/>
        <p className='about-text'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ipsam dolorum, optio aliquam ex dolorem inventore similique culpa.</p>
      </div>

      {/* category section */}

      <div className='category-section'>
        <p className='category-heading'>CATEGORIES</p>
        <ul className='category-list'>
          <li className='category-list-item'>Sports</li>
          <li className='category-list-item'>Music</li>
          <li className='category-list-item'>Science</li>
          <li className='category-list-item'>Food</li>
          <li className='category-list-item'>Nature</li>
          <li className='category-list-item'>Travel</li>
        </ul>
      </div>

      {/* Follow section */}

      <div className='follow-section'>
        <div className="follow-heading">FOLLOW US</div>
        <div className="follow-icons">
          <i class="fa-brands fa-facebook-square"></i>
        </div>
      </div>
      
    </div>
  )
}

export default Sidebar;
